const skipReason = 'Enhanced validation failed - maintaining character authenticity with speech analysis';
const validationData = $('Enhanced Voice Validator').item.json;

const logEntry = {
  timestamp: new Date().toISOString(),
  status: 'cycle_skipped_enhanced',
  reason: skipReason,
  topic: $('Enhanced Topic Generator').item.json.chosenTopic,
  context: $('Enhanced Topic Generator').item.json.postingContext,
  episode_analyzed: $('Enhanced Character Database').item.json.episodeData,
  validation_scores: {
    speech_pattern: validationData.speechPatternScore,
    authenticity: validationData.authenticityScore,
    overall: validationData.overallValidationScore
  },
  generated_tweet: validationData.generatedTweet,
  nextAttempt: 'Next scheduled cycle'
};

return [{
  json: {
    status: 'Cycle skipped - enhanced quality control with speech analysis',
    logEntry,
    message: 'Skipped posting to maintain character authenticity based on actual speech patterns',
    nextAttempt: 'Next scheduled cycle (6am or 4pm PT)',
    speechAnalysisVersion: 'v2.0'
  }
}];

const postResult = items[0].json;
const tweetData = $('Enhanced Safety Check').item.json;
const episodeInfo = $('Enhanced Character Database').item.json;

const logEntry = {
  timestamp: new Date().toISOString(),
  tweet: tweetData.finalTweet,
  characterCount: tweetData.characterCount,
  topic: $('Enhanced Topic Generator').item.json.chosenTopic,
  context: $('Enhanced Topic Generator').item.json.postingContext,
  episode_analyzed: episodeInfo.episodeData,
  speech_pattern_score: tweetData.speechPatternScore,
  authenticity_score: tweetData.authenticityScore,
  overall_validation: $('Enhanced Voice Validator').item.json.overallValidationScore,
  speech_analysis_applied: true,
  success: true
};

return [{
  json: {
    status: 'Tweet posted successfully with speech analysis',
    logEntry,
    message: `Character AI agent posted in authentic voice based on ${episodeInfo.episodeData} analysis`,
    nextPost: 'Next scheduled cycle (6am or 4pm PT)',
    speechAnalysisVersion: 'v2.0'
  }
}];

const tweetData = items[0].json;
const tweet = tweetData.generatedTweet;

const safetyChecks = {
  no_harmful_content: !/\\b(hate|violence|harm|attack|threat)\\b/i.test(tweet),
  no_personal_attacks: !/you (are|suck|fail)/i.test(tweet),
  family_friendly: !/\\b(damn|hell|shit|fuck)\\b/i.test(tweet),
  appropriate_content: true,
  no_spoilers: !/\\b(dies|killed|ending|finale)\\b/i.test(tweet),
  maintains_character: !/(I am an AI|This is a bot|Fictional character)/i.test(tweet)
};

const safetyScore = Object.values(safetyChecks).filter(check => check).length / Object.keys(safetyChecks).length;
const finalApproval = safetyScore >= 0.9;

return [{
  json: {
    finalTweet: tweet,
    characterCount: tweet.length,
    safetyScore: Math.round(safetyScore * 100) / 100,
    finalApproval,
    speechPatternScore: tweetData.speechPatternScore,
    authenticityScore: tweetData.authenticityScore,
    disclaimer: "Fan-created content inspired by Kirsh from Alien: Earth with speech analysis",
    timestamp: new Date().toISOString()
  }
}];

const agentResponse = items[0].json;
const characterData = $('Enhanced Topic Generator').item.json.enhancedCharacterProfile;

let generatedTweet = '';
if (agentResponse.output) {
  generatedTweet = agentResponse.output
    .replace(/^[\"']/g, '')
    .replace(/[\"']$/g, '')
    .replace(/^Tweet: /i, '')
    .replace(/^Here's a tweet: /i, '')
    .trim();
}

const speechPatternValidation = {
  appropriate_length: generatedTweet.length <= 280 && generatedTweet.length > 0,
  matches_sentence_length: (() => {
    const avgWordsPerSentence = generatedTweet.split(/[.!?]+/).filter(s => s.trim()).reduce((acc, sentence) => {
      return acc + sentence.trim().split(/\\s+/).length;
    }, 0) / Math.max(generatedTweet.split(/[.!?]+/).filter(s => s.trim()).length, 1);
    const targetAvg = characterData.speech_patterns.dialogue_characteristics.avg_sentence_length;
    return Math.abs(avgWordsPerSentence - targetAvg) <= 3;
  })(),
  simple_language: !/(consequently|nevertheless|furthermore|moreover|specifically)/i.test(generatedTweet),
  minimal_fillers: !/(like,|you know,|I mean,|basically,)/i.test(generatedTweet),
  measured_tone: !/(!{2,}|very |really |super |totally )/i.test(generatedTweet)
};

const authenticityChecks = {
  no_excessive_enthusiasm: !/(amazing|incredible|awesome|fantastic|exciting)/i.test(generatedTweet),
  understated_delivery: /\\b(not ideal|not bad|could be worse|fair enough|interesting|suppose|tends to|usually)\\b/i.test(generatedTweet) || generatedTweet.split('.').length <= 2,
  appropriate_brevity: generatedTweet.split(' ').length <= 25,
  no_overwording: !/\\b(extremely|incredibly|absolutely|definitely|completely)\\b/i.test(generatedTweet),
  matches_speech_flags: !/(fast|rushed|excited)/.test(generatedTweet.toLowerCase())
};

const speechScore = Object.values(speechPatternValidation).filter(check => check).length / Object.keys(speechPatternValidation).length;
const authenticityScore = Object.values(authenticityChecks).filter(check => check).length / Object.keys(authenticityChecks).length;
const overallValidationScore = (speechScore + authenticityScore) / 2;

const isValid = overallValidationScore >= 0.7 && generatedTweet.length > 0;

return [{
  json: {
    generatedTweet,
    characterCount: generatedTweet.length,
    speechPatternScore: Math.round(speechScore * 100) / 100,
    authenticityScore: Math.round(authenticityScore * 100) / 100,
    overallValidationScore: Math.round(overallValidationScore * 100) / 100,
    isValid,
    readyToPost: isValid && generatedTweet.length > 0,
    speechAnalysisApplied: true,
    timestamp: new Date().toISOString()
  }
}];

const avoidanceData = $('Analyze Recent Content').item.json;
const avoidanceRules = avoidanceData.avoidanceRules || [];
const recentTopics = avoidanceData.recentTopics || [];

const { enhancedCharacterProfile, postingContext } = items[0].json;

const topicPools = {
  survival_wisdom: [
    "why most people miss obvious warning signs",
    "the difference between being prepared and being paranoid", 
    "how panic spreads faster than actual danger",
    "recognizing when situations are about to change",
    "what separates survivors from casualties",
    "reading body language in tense situations"
  ],
  philosophical_observations: [
    "what humans tell themselves about being civilized",
    "why every generation thinks they are different", 
    "the gap between what people say and what they do",
    "patterns that repeat regardless of technology",
    "how fear changes decision making",
    "the animal instincts that never leave us"
  ],
  practical_advice: [
    "when to trust your gut versus logic",
    "building systems that work under pressure",
    "efficient communication in chaos", 
    "working with unreliable people",
    "making decisions with incomplete information",
    "the value of redundant backups"
  ],
  understated_wisdom: [
    "why most advice is situational",
    "the predictability of unpredictable people", 
    "common misconceptions about strength",
    "when silence is more powerful than words",
    "the cost of being right all the time",
    "what experience actually teaches you"
  ]
};

let selectedTopics = [];
if (postingContext === 'morning') {
  selectedTopics = [...topicPools.practical_advice, ...topicPools.survival_wisdom];
} else if (postingContext === 'afternoon') {
  selectedTopics = [...topicPools.philosophical_observations, ...topicPools.understated_wisdom];
} else if (postingContext === 'evening') {
  selectedTopics = [...topicPools.understated_wisdom, ...topicPools.survival_wisdom];
} else {
  selectedTopics = [...topicPools.philosophical_observations];
}

// Filter out recently used topics
const filteredTopics = selectedTopics.filter(topic => {
  const lowerTopic = topic.toLowerCase();
  return !recentTopics.some(recentTopic => 
    lowerTopic.includes(recentTopic.toLowerCase()) ||
    (recentTopic === 'planning' && lowerTopic.includes('plan')) ||
    (recentTopic === 'simplicity' && (lowerTopic.includes('simple') || lowerTopic.includes('basic'))) ||
    (recentTopic === 'leadership' && lowerTopic.includes('leader'))
  );
});

// Use filtered topics, fall back to original if empty
const finalTopics = filteredTopics.length > 0 ? filteredTopics : selectedTopics;
const chosenTopic = finalTopics[Math.floor(Math.random() * finalTopics.length)];

// Create strong avoidance prompt
const avoidancePrompt = avoidanceRules.length > 0 ? 
  `\n\nCRITICAL AVOIDANCE RULES - DO NOT IGNORE:\n${avoidanceRules.map(rule => `â€¢ ${rule}`).join('\n')}\n\nRecently covered topics to AVOID: ${recentTopics.join(', ')}\n` : '';

const contentPrompt = `Write a tweet about ${chosenTopic}.${avoidancePrompt}

IMPORTANT SPEECH CHARACTERISTICS TO MATCH:
- Speak at a measured pace (${Math.round(enhancedCharacterProfile.speech_patterns.pacing.overall_wpm)} WPM equivalent in text)
- Use simple, direct language (Grade ${enhancedCharacterProfile.speech_patterns.vocal_patterns.reading_level} reading level)
- Keep sentences short and impactful
- Strategic use of pauses (translate to punctuation/spacing)
- Minimal unnecessary words
- Understated delivery style

FRESH APPROACH REQUIRED:
- Use completely different sentence structures
- Avoid repetitive patterns from recent tweets
- Focus on unique observations or questions
- Channel Kirsh's measured, thoughtful voice

Stay completely in character using Kirsh's distinctive voice and measured speaking style.`;

return [{
  json: {
    chosenTopic,
    contentPrompt,
    postingContext,
    enhancedCharacterProfile,
    speechPatternsIncorporated: true,
    avoidanceRulesApplied: avoidanceRules.length,
    topicsFiltered: selectedTopics.length - finalTopics.length,
    availableTopics: finalTopics.length,
    timestamp: new Date().toISOString()
  }
}];

const allItems = items;
let speechFeatures = {};
let speechContext = {};
let speechFlags = '';
let episodeData = { latestEpisode: 'S01E01' };

// Add this debug section right after the variable declarations
console.log('DEBUG: Episode Data Fetcher output:', $('Episode Data Fetcher').item?.json);
const episodeSelection = $('Episode Data Fetcher').item?.json?.latestEpisode || 'S01E01';
console.log('DEBUG: episodeSelection value:', episodeSelection);

for (const item of allItems) {
  if (item.json) {
    // Check for combined data structure first
    if (item.json.wpm_overall !== undefined && item.json.segments && Array.isArray(item.json.segments)) {
      // This is combined speech data - extract both
      speechFeatures = item.json;
      speechContext = item.json;
    } else if (item.json.segments && Array.isArray(item.json.segments) && item.json.transcript) {
      // This is speech context data only
      speechContext = item.json;
    } else if (item.json.wpm_overall !== undefined) {
      // This is speech features data only
      speechFeatures = item.json;
    } else if (item.json.latestEpisode) {
      episodeData = { latestEpisode: item.json.latestEpisode };
    } else if (typeof item.json === 'string' && item.json.includes('slow')) {
      speechFlags = item.json;
    }
  }
  if (item.binary?.data) {
    try {
      speechFlags = Buffer.from(item.binary.data, 'base64').toString('utf8').trim();
    } catch (e) {
      console.log('Could not parse binary data');
    }
  }
}

if (!speechFeatures.wpm_overall) {
  speechFeatures = {
    wpm_overall: 107.6,
    wpm_articulation: 125.4,
    fillers_per_min: 0.5,
    pause_count: 76,
    segments: 30,
    words: 217,
    speech_time_sec: 103.83,
    pause_durations_sec: { mean: 0.23 },
    reading_level: { fk_grade: 4.24 }
  };
}

if (!speechContext.segments || !Array.isArray(speechContext.segments)) {
  speechContext = {
    segments: [
      { text: "What if, while I'm squashing it, another scorpion stings me to protect its friend the same way you're trying to protect me?" },
      { text: "Think of how the scorpion must feel, trapped under glass, menaced by giants." },
      { text: "Go on." },
      { text: "She's not human anymore. Why are we pretending she is?" },
      { text: "That's what it is to be an animal." }
    ]
  };
}

const actualDialogue = speechContext.segments?.map(seg => seg.text?.trim()).filter(text => text && text.length > 0) || [];

const speechAnalysis = {
  pacing: {
    overall_wpm: speechFeatures.wpm_overall || 107.6,
    articulation_wpm: speechFeatures.wpm_articulation || 125.4,
    is_slow_speaker: speechFlags.includes('slow_overall'),
    pause_frequency: speechFeatures.pause_count || 76
  },
  vocal_patterns: {
    filler_usage: speechFeatures.fillers_per_min || 0.5,
    reading_level: speechFeatures.reading_level?.fk_grade || 4.24,
    sentence_complexity: 'simple_direct'
  },
  dialogue_characteristics: {
    avg_sentence_length: speechFeatures.segments > 0 ? (speechFeatures.words / speechFeatures.segments) : 7.2,
    tends_toward_brevity: true,
    uses_pauses_effectively: (speechFeatures.pause_durations_sec?.mean || 0) > 0.2
  }
};

const enhancedCharacterProfile = {
  personality: {
    primary_traits: [
      "Stoic and unflappable",
      "Dry, deadpan humor", 
      "Authority figure with quiet confidence",
      "Pragmatic and direct",
      "Protective of others",
      "Experienced with danger",
      "Measured speaker - doesn't rush words",
      "Uses strategic pauses for emphasis"
    ],
    speaking_style: [
      "Short, clipped sentences",
      "Rarely shows emotion openly", 
      "Uses understatement for dramatic effect",
      "Direct and to the point",
      "Occasional dry quips",
      "Professional but not cold",
      `Speaks at ${Math.round(speechAnalysis.pacing.overall_wpm)} words per minute - deliberate pace`,
      "Minimal filler words - every word counts",
      "Simple, accessible language - no unnecessary complexity"
    ]
  },
  live_dialogue_samples: actualDialogue.slice(0, 10),
  speech_patterns: speechAnalysis,
  episode_context: {
    latest_episode: episodeSelection,
    total_speaking_time: speechFeatures.speech_time_sec || 103.83,
    content_themes_from_episode: [
      "Questions of humanity and identity",
      "Survival in hostile environments", 
      "Protection and sacrifice",
      "Evolution and adaptation",
      "Cycles of life and death"
    ]
  }
};

const currentTime = new Date();

// Convert to Pacific Time
const ptTime = new Date(currentTime.toLocaleString("en-US", {timeZone: "America/Los_Angeles"}));
const timeOfDay = ptTime.getHours();

let postingContext = 'general';
if (timeOfDay >= 6 && timeOfDay < 12) {
  postingContext = 'morning';
} else if (timeOfDay >= 12 && timeOfDay < 18) {
  postingContext = 'afternoon'; 
} else if (timeOfDay >= 18 && timeOfDay < 22) {
  postingContext = 'evening';
} else {
  postingContext = 'night';
}

return [{
  json: {
    enhancedCharacterProfile,
    postingContext,
    timeOfDay,
    ptTimeForDebug: ptTime.toISOString(),
    episodeData: episodeSelection,
    speechAnalysisApplied: true,
    timestamp: currentTime.toISOString()
  }
}];

const manifest = items[0].json;

// Validate manifest exists
if (!manifest) {
  return [{
    json: {
      latestEpisode: 'S01E01',
      analysisUrls: {
        features: 'https://kirshbot.banast.as/S01E01/analysis_features.json',
        context: 'https://kirshbot.banast.as/S01E01/analysis_context.json',
        flags: 'https://kirshbot.banast.as/S01E01/analysis_flags.txt'
      },
      error: 'No manifest data received',
      timestamp: new Date().toISOString()
    }
  }];
}

// Always use random selection from all available episodes
const availableEpisodes = manifest.episodes?.map(ep => ep.episode_id) || ['S01E01'];
const randomIndex = Math.floor(Math.random() * availableEpisodes.length);
const selectedEpisode = availableEpisodes[randomIndex];

const baseUrl = 'https://kirshbot.banast.as';
const analysisUrls = {
  features: `${baseUrl}/${selectedEpisode}/analysis_features.json`,
  context: `${baseUrl}/${selectedEpisode}/analysis_context.json`,
  flags: `${baseUrl}/${selectedEpisode}/analysis_flags.txt`
};

// Calculate days since update for logging purposes
const updatedAt = new Date(manifest.updated_at);
const now = new Date();
const daysSinceUpdate = (now - updatedAt) / (1000 * 60 * 60 * 24);

return [{
  json: {
    latestEpisode: selectedEpisode, // Now refers to randomly selected episode
    analysisUrls,
    manifest,
    daysSinceUpdate: Math.round(daysSinceUpdate * 10) / 10,
    selectionMethod: `random from ${availableEpisodes.length} episodes`,
    selectedIndex: randomIndex,
    availableEpisodes: availableEpisodes,
    manifestUpdatedAt: manifest.updated_at,
    timestamp: new Date().toISOString()
  }
}];

// Handle the weird Google Sheets structure
const recentRows = items || [];
const recentTweets = recentRows
  .map(row => {
    // The structure shows tweets are in the second property value
    const values = Object.values(row.json || {});
    // Skip row_number (first value), get the second value which contains the tweet
    return values[1] || null;
  })
  .filter(tweet => tweet && typeof tweet === 'string' && tweet.trim().length > 0)
  .slice(-15); // Last 15 tweets

console.log('Recent tweets found:', recentTweets.length);
console.log('Sample tweets:', recentTweets.slice(-3));

// Detect overused patterns
const patterns = {
  simple_complex: recentTweets.filter(t => 
    (t.includes('simple') || t.includes('Simple')) && 
    (t.includes('complicate') || t.includes('complex') || t.includes('Complicate') || t.includes('confusion'))
  ).length,

  three_sentence: recentTweets.filter(t => 
    t.split('.').filter(s => s.trim()).length >= 3
  ).length,

  plans_work: recentTweets.filter(t => 
    t.includes('plan') && (t.includes('work') || t.includes('succeed') || t.includes('follow') || t.includes('clear'))
  ).length,

  clear_direct: recentTweets.filter(t => 
    t.includes('clear') && t.includes('direct')
  ).length,

  keep_simple: recentTweets.filter(t => 
    t.includes('Keep') && (t.includes('simple') || t.includes('straightforward') || t.includes('basic'))
  ).length,

  x_y_format: recentTweets.filter(t => 
    t.match(/\w+\s+(succeed|are clear|work).*\w+\s+(create confusion|confuse|invite mistakes)/i)
  ).length
};

// Create avoidance rules based on actual patterns
const avoidanceRules = [];
if (patterns.simple_complex >= 2) {
  avoidanceRules.push('CRITICAL: Stop using simple vs complicated comparisons - heavily overused');
}
if (patterns.plans_work >= 2) {
  avoidanceRules.push('AVOID all planning/strategy topics - already covered multiple times');
}
if (patterns.clear_direct >= 1) {
  avoidanceRules.push('Do not use clear and direct phrasing - just used');
}
if (patterns.keep_simple >= 1) {
  avoidanceRules.push('Avoid Keep it simple/straightforward advice - just posted');
}
if (patterns.x_y_format >= 2) {
  avoidanceRules.push('STOP using X succeeds/works, Y creates confusion format - overused pattern');
}

// Extract topics from actual tweets
const recentTopics = [];
recentTweets.forEach(tweet => {
  const lowerTweet = tweet.toLowerCase();
  if (lowerTweet.includes('plan')) recentTopics.push('planning');
  if (lowerTweet.includes('simple') || lowerTweet.includes('basic') || lowerTweet.includes('straightforward')) recentTopics.push('simplicity');
  if (lowerTweet.includes('clear') || lowerTweet.includes('direct')) recentTopics.push('clarity');
  if (lowerTweet.includes('leadership') || lowerTweet.includes('leader')) recentTopics.push('leadership');
  if (lowerTweet.includes('evolution') || lowerTweet.includes('change')) recentTopics.push('evolution');
  if (lowerTweet.includes('history') || lowerTweet.includes('repeat')) recentTopics.push('history');
  if (lowerTweet.includes('patience') || lowerTweet.includes('difficult people')) recentTopics.push('patience');
});

return [{
  json: {
    recentTweets: recentTweets,
    avoidanceRules,
    recentTopics: [...new Set(recentTopics)],
    patternCounts: patterns,
    totalRecentTweets: recentTweets.length,
    analysisTimestamp: new Date().toISOString(),
    debugInfo: {
      sampleStructure: recentRows[0]?.json || {},
      extractedSample: recentTweets.slice(-2)
    }
  }
}];
